@media (max-width: 600px) {
    html {
       margin-left: 0;
	margin-right: 0;
    }
    #overall {
       margin-left: 0;
       margin-right: 0;   
 }
}

#overall {
 font-family: "Noto Serif", Junicode, Gentium, serif; 
 font-size: 2em;
 color: white;
 background-color: #002;
}

#main {
  z-index: 1;
  position: absolute;
}

#sub {
  position: relative;
  z-index: 3;
  opacity: 0.3;
  float: left;
  margin-bottom: 15px;
  border: 1px solid black;
  clear: both;
  background-color: blue;
}
